<style>
  #experience {
    padding: 60px 20px 100px;
    position: relative;
    overflow: hidden;
  }

  .content-wrapper {
    max-width: 1100px;
    margin: 0 auto;
    width: 100%;
  }

  .grid-layout {
    display: grid;
    grid-template-columns: 1fr 1.5fr;
    gap: 80px;
    align-items: start;
  }

  .visual-column {
    position: sticky;
    top: 150px;
  }

  .header-group {
    opacity: 0;
    transform: translateY(30px);
    transition: all 1s cubic-bezier(0.16, 1, 0.3, 1);
  }

  .section-title {
    font-size: 2.5rem;
    font-weight: 700;
    color: #fff;
    margin: 0;
    background: linear-gradient(to right, #fff, #a5a5a5);
    -webkit-background-clip: text;
    background-clip: text;
    -webkit-text-fill-color: transparent;
    letter-spacing: -0.02em;
  }

  .section-subtitle {
    font-size: 1.1rem;
    color: rgba(255, 255, 255, 0.6);
    margin-top: 16px;
    line-height: 1.6;
    font-family: -apple-system, BlinkMacSystemFont, "SF Pro Display", "Segoe UI",
      sans-serif;
  }

  /* Graphic Container & Architecture SVG */
  .graphic-container {
    position: relative;
    margin-top: 40px;
    height: 300px;
    display: flex;
    align-items: center;
    justify-content: center;
  }

  .glow {
    position: absolute;
    top: 50%;
    left: 50%;
    width: 250px;
    height: 250px;
    background: rgba(76, 201, 240, 0.15); /* Tech blue glow matching Hero */
    filter: blur(70px);
    transform: translate(-50%, -50%);
    border-radius: 50%;
    z-index: 0;
  }

  .system-orchestration {
    position: relative;
    width: 100%;
    height: 100%;
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 1;
    opacity: 0;
    transform: translateY(30px) scale(0.96);
    transition: all 1.2s cubic-bezier(0.16, 1, 0.3, 1) 0.15s;
  }

  #experience.visible .system-orchestration {
    opacity: 1;
    transform: translateY(0) scale(1);
  }

  .connection-lines {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 400px;
    height: 400px;
    pointer-events: none;
    z-index: 0;
  }

  .central-core {
    position: absolute;
    width: 80px;
    height: 80px;
    background: rgba(25, 25, 25, 0.8);
    border: 1px solid rgba(76, 201, 240, 0.4);
    border-radius: 20px; /* Squircle */
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 2;
    box-shadow:
      0 10px 30px rgba(0, 0, 0, 0.5),
      inset 0 1px 0 rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(12px);
    -webkit-backdrop-filter: blur(12px);
  }

